customCss:
@import url("https://fontsapi.zeoseven.com/442/main/result.css");
#theme-dock { display: none; }
body { 
    font-family: 'Maple Mono NF CN' !important; 
    background: rgb(27 27 30) !important;
    --font-mono: 'Maple Mono NF CN' !important;
    --font-sans: 'Maple Mono NF CN' !important;
}
.panel {
    background: transparent;
    border: none;
    box-shadow: none;
}
* {
    --accent: rgb(116 178 243) !important;
    --bg-1: #11151b !important;
    --bg-2: #181f29 !important;
    --ink: #d3dbe8 !important;
    --ink-2: #b4c0d0 !important;
    --muted: #8592a3 !important;
    --panel: rgba(24, 33, 43, .92) !important;
    --line: rgba(131, 154, 188, .14) !important;
    --accent-glow: rgba(149, 170, 202, .2) !important;
    --code-bg: rgba(149, 170, 202, .14) !important;
}
blockquote {
    background: none !important;
}
.post-item {
    background: #1e1e1e;
    border-radius: 10px;
    padding: 20px;
    box-shadow: rgb(21 21 21 / 72%) 0 6px 18px 0, rgb(137 135 135 / 24%) 0 0 0 1px;
}
.site-home-shell .link-button[href="/admin"] {
    display: none;
}
.reaction-btn {
    background: #1e1e1e;
}
.reaction-label { color: var(--ink); }
.article-wrap, .community-grid {
    display: block;
}
.comment-list {
    display: flex;
    flex-direction: column-reverse;
}
