customCss:
@import url("https://fontsapi.zeoseven.com/442/main/result.css");
#theme-dock { display: none; }
body { 
    font-family: 'Maple Mono NF CN' !important; 
    background: rgb(116 178 243) !important;
}
.panel {
    background: transparent;
    border: none;
    box-shadow: none;
}
* {
    --accent: rgb(63 81 181);
}
blockquote {
    background: none !important;
}
.post-item {
    background: #1e1e1e;
    border-radius: 10px;
    padding: 20px;
    box-shadow: rgb(21 21 21 / 72%) 0 6px 18px 0, rgb(137 135 135 / 24%) 0 0 0 1px;
}
.site-home-shell .link-button[href="/admin"] {
    display: none;
}
.reaction-btn {
    background: #1e1e1e;
}
.reaction-label { color: var(--ink); }
.article-wrap {
    display: block;
    grid-template-columns: unset;
    gap: unset;
}
